space_text = "Hi!    This      is        Furkan."text = "Hi! This is Furkan."special_ch_text= "#@Hi! This is Furkan.@%"wrong_text = "Hi! Thiss is John."html_text = "<div> Hi! This is Furkan.</div>"# %% metinlerde bulunan fazla bosluklari ortadan akldircleaned_text = space_text.split()print("".join(cleaned_text))# %% buyuk -> kucuk harf cevrimicleaned_text_1 = text.lower()print(cleaned_text_1)# %% noktalama isaretlerini kaldir import stringcleaned_text_2 = text.translate(str.maketrans("","", string.punctuation))print(cleaned_text_2)# %% ozel karakterleri kaldir --> %, @, /, *, #import re cleaned_text_3 = re.sub(r"[^A-Za-z0-9\s]", "", special_ch_text)print(cleaned_text_3)# %% yazim hatalarını duzeltfrom textblob import TextBlobcleaned_text_4 = TextBlob(wrong_text).correct()print(cleaned_text_4)# %% html ya da url etiketlerini kaldirfrom bs4 import BeautifulSoup'''** BeautifulSoup ile html yapsını parse et, get_text ile text kismini çek. **'''cleaned_text_5 = BeautifulSoup(html_text, "html.parser").get_text()print(cleaned_text_5)