import nltkfrom nltk.stem import PorterStemmer # stemming i√ßin fonksiyonfrom nltk.stem import WordNetLemmatizer# %% stemmingnltk.download("wordnet") # wordnet: lemmatization islemi icin gerekli veritabani# porter stemmer nesnesini olusturstemmer = PorterStemmer()words = ["running", "runner", "ran", "runs", "better", "go", "went"]# kelimelerin stem' lerini bulmastems = [stemmer.stem(w) for w in words]print(f"stems: {stems}")# %% lemmatizationlemmatizer = WordNetLemmatizer()words_1 = ["running", "runner", "ran", "runs", "better", "go", "went"]lemmas = [lemmatizer.lemmatize(w, pos="v") for w in words]print(f"lemas: {lemmas}")