import nltkfrom nltk.corpus import stopwordsnltk.download("stopwords") # farklı dillerde en cok kullanilan stop words iceren veri seti# ingilizce stop_words analizi (nltk)eng_stop_words = set(stopwords.words("english"))# örnek ingilizce metineng_text = "There are some examples of handling stop words from some texts."text_list = eng_text.split()# eğer kelime stop words listesinde yoksa filtered_list' e ekliyoruzfiltered_list = [word for word in text_list if word.lower() not in eng_stop_words]print(f"filtered list: {filtered_list}")# türkçe stop_words analizi (nltk)tr_stop_words = set(stopwords.words("turkish"))tr_text = "Merhaba arkadaşlar çok güzel bir ders işliyoruz"text_list_tr = tr_text.split()filtered_list_tr = [word for word in text_list_tr if word.lower() not in tr_stop_words]print(f"filtered list tr : {filtered_list_tr}")# kütüphanesiz stop_words filtelemesitr_self_stop_words = ["için", "bu", "ile", "mu", "mi", "özel"]self_text = "Bu bir denemedir. Amacımız bu metinde bulunan özel karakterleri elemek mi acaba?"self_filtered_list = [word.lower() for word in self_text.split() if word.lower() not in tr_self_stop_words]print(f"filtered list self : {self_filtered_list}")